          plaid_token: details
        });
      break;
    case "institutionname":
      firebase
        .database()
        .ref(`/users/${currentUser.currentUseruid}/userDetails`)
        .update({
          institution_name: details
        });
      break;
    case "institutionid":
      firebase
        .database()
        .ref(`/users/${currentUser.currentUseruid}/userDetails`)
        .update({
          institution_id: details
        });
      break;
    case "plaidaccountnumber":
      firebase
        .database()
        .ref(`/users/${currentUser.currentUseruid}/userDetails`)
        .update({
          plaid_last_four: details
        });
      break;
    case "plaidaccounttype":
      firebase
        .database()
        .ref(`/users/${currentUser.currentUseruid}/userDetails`)
        .update({
          plaid_account_type: details
        });
      break;

    case "plaidaccountid":
      firebase
        .database()
        .ref(`/users/${currentUser.currentUseruid}/userDetails`)
        .update({
          plaid_account_id: details
        });
      break;

    case "numberofappointmentscompleted":
      firebase
        .database()
        .ref(`/users/${currentUser.currentUseruid}/userDetails`)
        .update({
          numofapptcompleted: details
        });
      break;
      case "isCleaner":
        firebase
          .database()
          .ref(`/users/${currentUser.currentUseruid}/userDetails`)
          .update({
            isCleaner: details
          });
        break;
  }
}

